class Solution {
public:
    bool canJump(vector<int>& nums) {
        int index = 0;
        int NumOfZero = 0;
        int SizeOfNums = nums.size();
        bool can = false;
        if(SizeOfNums == 1) return true;
        if(nums[0] == 0) return false;
        for(int i = 0; i < SizeOfNums; i++){
            if(nums[i] == 0){
                index = i;
                while(index < SizeOfNums && nums[index] == 0){
                    NumOfZero++;
                    index++;
                }
                for(int j = i - 1; j >= 0; j--){
                   can = can || (nums[j] > NumOfZero + i - 1 - j) || (nums[j] + j ==  SizeOfNums - 1);
                }
                if(!can) return can;
                can = false;
                NumOfZero = 0;
                i = index; 
            }
            if(i >= SizeOfNums - 1)return true;
            if(i + nums[i] >= SizeOfNums - 1)return true;
            i = i + nums[i] - 1;
        }      
        return !can;
        
    }
};


